	private static int decode0(char ibuf[], byte obuf[], int wp) {
		int outlen = 3;
		if (ibuf[3] == '=')
			outlen = 2;
		if (ibuf[2] == '=')
			outlen = 1;
		int b0 = S_DECODETABLE[ibuf[0]];
		int b1 = S_DECODETABLE[ibuf[1]];
		int b2 = S_DECODETABLE[ibuf[2]];
		int b3 = S_DECODETABLE[ibuf[3]];
		switch (outlen) {
		case 1: // '\001'
			obuf[wp] = (byte) (b0 << 2 & 252 | b1 >> 4 & 3);
			return 1;

		case 2: // '\002'
			obuf[wp++] = (byte) (b0 << 2 & 252 | b1 >> 4 & 3);
			obuf[wp] = (byte) (b1 << 4 & 240 | b2 >> 2 & 15);
			return 2;

		case 3: // '\003'
			obuf[wp++] = (byte) (b0 << 2 & 252 | b1 >> 4 & 3);
			obuf[wp++] = (byte) (b1 << 4 & 240 | b2 >> 2 & 15);
			obuf[wp] = (byte) (b2 << 6 & 192 | b3 & 63);
			return 3;
		}
		throw new RuntimeException("Internal error");
	}

	/**
	 * 签名算法
	 *
	 * @author HXS
	 * @date 2012-7-2
	 * @todo TODO
	 * @param data
	 *            需要签名的内容
	 * @param keyStoreFileName
	 *            含私钥的文件
	 * @param keyStorePassword
	 *            含私钥文件的密码
	 * @param keyStoreAlias
	 *            别名
	 * @return
	 */
	public static String signData(String data, String keyStoreFileName,
                                  String keyStorePassword, String keyStoreAlias) {
		KeyStore keyStore;
		byte[] signRstByte = null;
		String signValue = "";
		String keystoreType = "";
		try {
			if (keyStoreFileName.toUpperCase().indexOf("PFX") >= 0)// 判断证书文件的格式
			{
				keystoreType = "PKCS12";
			} else {
				keystoreType = "JKS";
			}
			keyStore = KeyStore.getInstance(keystoreType);// 获取JKS证书实例
			FileInputStream in = new FileInputStream(keyStoreFileName);// 获取证书文件流
			char[] pwdChar = keyStorePassword.toCharArray();// 证书密码
			keyStore.load(in, pwdChar);// 加载证书到keystore中
			PrivateKey privateKey = (PrivateKey) keyStore.getKey(keyStoreAlias,
					pwdChar);// 从证书中获取私钥
			Signature sign = Signature.getInstance("SHA1WithRSA");// SHA1WithRSA签名算法
			sign.initSign(privateKey);// 设置私钥
			sign.update(data.getBytes());// 设置明文
			signRstByte = sign.sign();// 加密
			BASE64Encoder encoder = new BASE64Encoder();
			signValue = encoder.encodeBuffer(signRstByte);// BASE64编码
			// System.out.println("签名并编码后的内容signValue=="+signValue);
		} catch (KeyStoreException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (FileNotFoundException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (NoSuchAlgorithmException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (CertificateException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (UnrecoverableKeyException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (InvalidKeyException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (SignatureException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		return signValue;

	}

	public static byte[] decode(String data) {
		char ibuf[] = new char[4];
		int ibufcount = 0;
		byte obuf[] = new byte[(data.length() / 4) * 3 + 3];
		int obufcount = 0;
		for (int i = 0; i < data.length(); i++) {
			char ch = data.charAt(i);
			if (ch != '='
					&& (ch >= S_DECODETABLE.length || S_DECODETABLE[ch] == 127))
				continue;
			ibuf[ibufcount++] = ch;
			if (ibufcount == ibuf.length) {
				ibufcount = 0;
				obufcount += decode0(ibuf, obuf, obufcount);
			}
		}

		if (obufcount == obuf.length) {
			return obuf;
		} else {
			byte ret[] = new byte[obufcount];
			System.arraycopy(obuf, 0, ret, 0, obufcount);
			return ret;
		}
	}

